import{_ as t,c as a,o,af as d}from"./chunks/framework.DHd1b-5X.js";const b=JSON.parse('{"title":"产检时间表与关键指标","description":"","frontmatter":{},"headers":[],"relativePath":"prenatal-care-guides/checkups/prenatal-checkups.md","filePath":"prenatal-care-guides/checkups/prenatal-checkups.md"}'),l={name:"prenatal-care-guides/checkups/prenatal-checkups.md"};function r(c,e,i,n,s,h){return o(),a("div",null,[...e[0]||(e[0]=[d('<h1 id="产检时间表与关键指标" tabindex="-1">产检时间表与关键指标 <a class="header-anchor" href="#产检时间表与关键指标" aria-label="Permalink to “产检时间表与关键指标”">​</a></h1><h2 id="产检时间安排" tabindex="-1">产检时间安排 <a class="header-anchor" href="#产检时间安排" aria-label="Permalink to “产检时间安排”">​</a></h2><table tabindex="0"><thead><tr><th>孕周</th><th>检查项目</th><th>重要提示</th></tr></thead><tbody><tr><td>6-8周</td><td>首次产检、B超确认宫内妊娠</td><td>建立孕期档案</td></tr><tr><td>12周</td><td>NT检查、早期唐筛</td><td>评估染色体异常风险</td></tr><tr><td>16周</td><td>中期唐筛</td><td>评估21三体综合征风险</td></tr><tr><td>20-24周</td><td>四维彩超大排畸</td><td>检查胎儿结构异常</td></tr><tr><td>24-28周</td><td>糖耐量试验(GTT)</td><td>筛查妊娠糖尿病</td></tr><tr><td>30-32周</td><td>胎儿生长发育评估</td><td>监测胎儿体重增长</td></tr><tr><td>34周</td><td>胎心监护(NST)</td><td>评估胎儿宫内状况</td></tr><tr><td>37周后</td><td>每周产检</td><td>密切监测分娩征象</td></tr></tbody></table><h3 id="产检频率建议" tabindex="-1">产检频率建议 <a class="header-anchor" href="#产检频率建议" aria-label="Permalink to “产检频率建议”">​</a></h3><ul><li>孕28周前：每4周一次</li><li>孕28-36周：每2周一次</li><li>孕36周后：每周一次</li></ul><h2 id="关键检查项目及正常值" tabindex="-1">关键检查项目及正常值 <a class="header-anchor" href="#关键检查项目及正常值" aria-label="Permalink to “关键检查项目及正常值”">​</a></h2><h3 id="血常规检查" tabindex="-1">血常规检查 <a class="header-anchor" href="#血常规检查" aria-label="Permalink to “血常规检查”">​</a></h3><ul><li><strong>血红蛋白(Hb)</strong>：≥<code>110g/L</code>(孕早期)、≥<code>105g/L</code>(孕晚期)</li><li><strong>血小板(PLT)</strong>：(<code>100-300</code>)×<code>10⁹/L</code></li><li><strong>白细胞(WBC)</strong>：(<code>4-10</code>)×<code>10⁹/L</code></li></ul><h4 id="异常警示" tabindex="-1">异常警示 <a class="header-anchor" href="#异常警示" aria-label="Permalink to “异常警示”">​</a></h4><blockquote><p>⚠️ <strong>注意</strong>：</p><ul><li><code>Hb</code>&lt;<code>100g/L</code>提示贫血，需补充铁剂</li><li><code>PLT</code>&lt;<code>50×10⁹/L</code>可能影响凝血功能</li></ul></blockquote><h3 id="尿常规检查" tabindex="-1">尿常规检查 <a class="header-anchor" href="#尿常规检查" aria-label="Permalink to “尿常规检查”">​</a></h3><ul><li><strong>尿蛋白</strong>：阴性(<code>-</code>)或微量(<code>±</code>)</li><li><strong>尿糖</strong>：阴性(<code>-</code>)</li><li><strong>白细胞</strong>：<code>0-5</code>个/高倍视野</li></ul><h4 id="异常警示-1" tabindex="-1">异常警示 <a class="header-anchor" href="#异常警示-1" aria-label="Permalink to “异常警示”">​</a></h4><blockquote><p>⚠️ <strong>重要提醒</strong>：</p><ul><li>尿蛋白≥(<code>+</code>)<strong>可能提示妊娠高血压</strong></li><li>尿糖阳性需进一步检查血糖</li></ul></blockquote><h3 id="生化检查" tabindex="-1">生化检查 <a class="header-anchor" href="#生化检查" aria-label="Permalink to “生化检查”">​</a></h3><ul><li><strong>空腹血糖(FBG)</strong>：&lt;<code>5.1mmol/L</code></li><li><strong>ALT/AST</strong>：&lt;正常上限<code>2</code>倍</li><li><strong>肌酐(Cr)</strong>：&lt;<code>70μmol/L</code></li></ul><h4 id="异常警示-2" tabindex="-1">异常警示 <a class="header-anchor" href="#异常警示-2" aria-label="Permalink to “异常警示”">​</a></h4><blockquote><p>⚠️ <strong>重点关注</strong>：</p><ul><li><code>FBG</code>≥<code>5.1mmol/L</code>可能诊断妊娠糖尿病</li><li><code>ALT</code>/<code>AST</code>升高可能提示肝脏损害</li></ul></blockquote><h3 id="胎儿监测指标" tabindex="-1">胎儿监测指标 <a class="header-anchor" href="#胎儿监测指标" aria-label="Permalink to “胎儿监测指标”">​</a></h3><ul><li><strong>胎心率</strong>：<code>120-160</code>次/分钟</li><li><strong>羊水量</strong>：最大羊水池深度<code>2-8cm</code></li><li><strong>胎盘成熟度</strong>：孕晚期<code>0-Ⅱ</code>级为正常</li></ul><h4 id="异常警示-3" tabindex="-1">异常警示 <a class="header-anchor" href="#异常警示-3" aria-label="Permalink to “异常警示”">​</a></h4><blockquote><p>⚠️ <strong>紧急情况</strong>：</p><ul><li>胎心率持续&lt;<code>110</code>或&gt;<code>160</code>次/分钟需紧急处理</li><li>羊水过少(&lt;<code>2cm</code>)或过多(&gt;<code>8cm</code>)需查找原因</li></ul></blockquote><h3 id="血压监测" tabindex="-1">血压监测 <a class="header-anchor" href="#血压监测" aria-label="Permalink to “血压监测”">​</a></h3><ul><li><strong>正常血压</strong>：&lt;<code>140/90mmHg</code></li><li><strong>妊娠高血压</strong>：≥<code>140/90mmHg</code>伴蛋白尿</li><li><strong>子痫前期</strong>：血压≥<code>140/90mmHg</code>伴蛋白尿≥<code>0.3g/24h</code></li></ul><h4 id="重要提醒" tabindex="-1">重要提醒 <a class="header-anchor" href="#重要提醒" aria-label="Permalink to “重要提醒”">​</a></h4><blockquote><p>⚡ <strong>每次产检都应测量血压和体重</strong>，这是发现妊娠高血压综合征的重要手段。</p></blockquote>',26)])])}const u=t(l,[["render",r]]);export{b as __pageData,u as default};
