import{_ as a,c as i,o as e,af as r}from"./chunks/framework.DHd1b-5X.js";const b=JSON.parse('{"title":"分娩前征象识别","description":"","frontmatter":{},"headers":[],"relativePath":"prenatal-care-guides/labor-signs/signs-of-labor.md","filePath":"prenatal-care-guides/labor-signs/signs-of-labor.md"}'),o={name:"prenatal-care-guides/labor-signs/signs-of-labor.md"};function n(s,l,t,d,c,h){return e(),i("div",null,[...l[0]||(l[0]=[r('<h1 id="分娩前征象识别" tabindex="-1">分娩前征象识别 <a class="header-anchor" href="#分娩前征象识别" aria-label="Permalink to “分娩前征象识别”">​</a></h1><h3 id="临产征象" tabindex="-1">临产征象 <a class="header-anchor" href="#临产征象" aria-label="Permalink to “临产征象”">​</a></h3><h4 id="真正临产的三大征象" tabindex="-1">真正临产的三大征象 <a class="header-anchor" href="#真正临产的三大征象" aria-label="Permalink to “真正临产的三大征象”">​</a></h4><ol><li><p><strong>规律宫缩</strong></p><ul><li>特点：持续时间逐渐延长(<code>30-60</code>秒)，间歇时间逐渐缩短(<code>5-3</code>分钟)</li><li>强度：疼痛感逐渐加强，不能通过休息或改变体位缓解</li><li>频率：每小时宫缩次数≥<code>4</code>次</li></ul></li><li><p><strong>见红</strong></p><ul><li>表现：阴道排出少量血性分泌物，呈粉红色或褐色</li><li>时间：通常在临产前<code>24-48</code>小时内出现</li><li>区别：与病理性的鲜红色出血不同</li></ul></li><li><p><strong>破水</strong></p><ul><li>表现：阴道突然流出大量无色透明液体</li><li>处理：无论是否有宫缩都应立即平躺并就医</li><li>注意：保持外阴清洁，避免感染</li></ul></li></ol><h4 id="先兆临产表现" tabindex="-1">先兆临产表现 <a class="header-anchor" href="#先兆临产表现" aria-label="Permalink to “先兆临产表现”">​</a></h4><ul><li><strong>胎儿下降感</strong>：孕妇感觉上腹部轻松，呼吸改善，食欲好转</li><li><strong>不规律宫缩</strong>：持续时间短且不恒定，间歇时间长且不规律</li><li><strong>见红</strong>：少量血性分泌物，但无规律宫缩</li></ul><h4 id="需要立即就医的情况" tabindex="-1">需要立即就医的情况 <a class="header-anchor" href="#需要立即就医的情况" aria-label="Permalink to “需要立即就医的情况”">​</a></h4><ul><li><strong>阴道大量出血</strong>：鲜红色血液，量超过平时月经量</li><li><strong>胎动异常</strong>：胎动明显减少或过于频繁</li><li><strong>剧烈腹痛</strong>：持续性腹痛伴有阴道出血</li><li><strong>破水</strong>：羊水流出，不论是否有宫缩</li><li><strong>严重头痛、视物模糊</strong>：可能为子痫前期表现</li></ul><h3 id="入院准备清单" tabindex="-1">入院准备清单 <a class="header-anchor" href="#入院准备清单" aria-label="Permalink to “入院准备清单”">​</a></h3><h4 id="证件资料类" tabindex="-1">证件资料类 <a class="header-anchor" href="#证件资料类" aria-label="Permalink to “证件资料类”">​</a></h4><ul><li>身份证原件及复印件</li><li>医保卡/生育保险凭证</li><li>产检病历及所有检查报告</li><li>准生证(部分地区需要)</li><li>银行卡及现金</li></ul><h4 id="妈妈用品类" tabindex="-1">妈妈用品类 <a class="header-anchor" href="#妈妈用品类" aria-label="Permalink to “妈妈用品类”">​</a></h4><ul><li><strong>衣物</strong>：哺乳内衣<code>2-3</code>件、出院服<code>1</code>套、拖鞋<code>1</code>双</li><li><strong>卫生用品</strong>：产妇卫生巾、护理垫、一次性内裤</li><li><strong>洗漱用品</strong>：毛巾、牙刷、牙膏、护肤品</li><li><strong>餐具</strong>：吸管杯、保温饭盒</li><li><strong>其他</strong>：手机充电器、笔记本和笔</li></ul><h4 id="宝宝用品类" tabindex="-1">宝宝用品类 <a class="header-anchor" href="#宝宝用品类" aria-label="Permalink to “宝宝用品类”">​</a></h4><ul><li><strong>衣物</strong>：新生儿衣服<code>3-5</code>套、帽子<code>1-2</code>顶、包被<code>1-2</code>条</li><li><strong>卫生用品</strong>：<code>NB</code>码纸尿裤<code>1</code>包、湿纸巾、棉柔巾</li><li><strong>喂养用品</strong>：奶瓶<code>2</code>个、奶粉小罐(备用)、小勺子</li><li><strong>其他</strong>：婴儿指甲剪、体温计</li></ul><h4 id="待产包打包建议" tabindex="-1">待产包打包建议 <a class="header-anchor" href="#待产包打包建议" aria-label="Permalink to “待产包打包建议”">​</a></h4><ul><li>提前在孕<code>36</code>周左右准备好所有物品</li><li>将物品分类装袋，贴上标签便于取用</li><li>重要证件随身携带，不要放入行李箱</li><li>准备一个小型背包放置急需物品，随时可带走</li></ul>',17)])])}const u=a(o,[["render",n]]);export{b as __pageData,u as default};
