import{_ as t,c as r,o as i,af as e}from"./chunks/framework.DHd1b-5X.js";const p=JSON.parse('{"title":"产后恢复护理","description":"","frontmatter":{},"headers":[],"relativePath":"prenatal-care-guides/postpartum-care/postpartum-recovery.md","filePath":"prenatal-care-guides/postpartum-care/postpartum-recovery.md"}'),o={name:"prenatal-care-guides/postpartum-care/postpartum-recovery.md"};function a(d,l,n,s,c,g){return i(),r("div",null,[...l[0]||(l[0]=[e('<h1 id="产后恢复护理" tabindex="-1">产后恢复护理 <a class="header-anchor" href="#产后恢复护理" aria-label="Permalink to “产后恢复护理”">​</a></h1><h3 id="身体康复" tabindex="-1">身体康复 <a class="header-anchor" href="#身体康复" aria-label="Permalink to “身体康复”">​</a></h3><h4 id="产后生理变化与恢复" tabindex="-1">产后生理变化与恢复 <a class="header-anchor" href="#产后生理变化与恢复" aria-label="Permalink to “产后生理变化与恢复”">​</a></h4><p><strong>子宫复旧</strong>：</p><ul><li>产后第一天子宫底平脐，以后每天下降<code>1-2cm</code></li><li>产后<code>6</code>周子宫恢复至孕前大小</li><li>恶露变化：血性→浆液性→白色，总持续<code>4-6</code>周</li></ul><p><strong>伤口护理</strong>：</p><ul><li><strong>顺产侧切/撕裂</strong>：保持清洁干燥，每日温水清洗</li><li><strong>剖宫产切口</strong>：避免沾水，观察有无红肿渗液</li><li><strong>正常愈合时间</strong>：<code>7-10</code>天拆线，完全愈合需<code>4-6</code>周</li></ul><p><strong>乳房护理</strong>：</p><ul><li>产后<code>30</code>分钟内开始哺乳</li><li>每次哺乳前后清洁乳头</li><li>预防乳腺炎：及时排空乳汁，避免淤积</li></ul><h4 id="产后检查时间表" tabindex="-1">产后检查时间表 <a class="header-anchor" href="#产后检查时间表" aria-label="Permalink to “产后检查时间表”">​</a></h4><table tabindex="0"><thead><tr><th>时间</th><th>检查项目</th><th>重点关注</th></tr></thead><tbody><tr><td>产后3天</td><td>首次家访</td><td>子宫收缩、恶露情况</td></tr><tr><td>产后14天</td><td>居家观察</td><td>伤口愈合、乳房状况</td></tr><tr><td>产后28天</td><td>社区检查</td><td>体重、血压、一般情况</td></tr><tr><td>产后42天</td><td>医院复查</td><td>全面评估恢复情况</td></tr></tbody></table><h4 id="产后异常情况处理" tabindex="-1">产后异常情况处理 <a class="header-anchor" href="#产后异常情况处理" aria-label="Permalink to “产后异常情况处理”">​</a></h4><p><strong>需要就医的症状</strong>：</p><ul><li>发热(体温&gt;<code>38℃</code>)</li><li>恶露异味或持续血性超过<code>10</code>天</li><li>伤口红肿、化脓、剧烈疼痛</li><li>严重便秘或腹泻</li><li>头痛、视物模糊、血压升高</li></ul><h3 id="心理调适" tabindex="-1">心理调适 <a class="header-anchor" href="#心理调适" aria-label="Permalink to “心理调适”">​</a></h3><h4 id="产后情绪变化阶段" tabindex="-1">产后情绪变化阶段 <a class="header-anchor" href="#产后情绪变化阶段" aria-label="Permalink to “产后情绪变化阶段”">​</a></h4><ol><li><p><strong>依赖期</strong>(产后1-3天)</p><ul><li>特点：依赖性强，情绪波动大</li><li>护理：给予理解和支持，协助适应母亲角色</li></ul></li><li><p><strong>依赖-独立期</strong>(产后3-14天)</p><ul><li>特点：情绪不稳定，易哭泣焦虑</li><li>护理：耐心倾听，提供情感支持</li></ul></li><li><p><strong>独立期</strong>(产后14天-1个月)</p><ul><li>特点：信心增强，逐渐独立照顾婴儿</li><li>护理：鼓励自主决策，培养育儿技能</li></ul></li></ol><h4 id="产后抑郁识别与应对" tabindex="-1">产后抑郁识别与应对 <a class="header-anchor" href="#产后抑郁识别与应对" aria-label="Permalink to “产后抑郁识别与应对”">​</a></h4><p><strong>常见症状</strong>：</p><ul><li>持续悲伤、绝望感</li><li>对婴儿缺乏兴趣</li><li>睡眠障碍(不是因婴儿夜间哭闹)</li><li>食欲改变</li><li>自我价值感低下</li></ul><p><strong>应对措施</strong>：</p><ul><li>家人理解和陪伴至关重要</li><li>保证充足休息和营养</li><li>适当的户外活动</li><li>严重时寻求专业心理帮助</li></ul><h3 id="新生儿护理" tabindex="-1">新生儿护理 <a class="header-anchor" href="#新生儿护理" aria-label="Permalink to “新生儿护理”">​</a></h3><h4 id="日常护理要点" tabindex="-1">日常护理要点 <a class="header-anchor" href="#日常护理要点" aria-label="Permalink to “日常护理要点”">​</a></h4><p><strong>喂养指导</strong>：</p><ul><li><strong>母乳喂养</strong>：按需哺乳，新生儿每日<code>8-12</code>次</li><li><strong>人工喂养</strong>：按体重计算奶量，每<code>3-4</code>小时一次</li><li><strong>混合喂养</strong>：优先母乳，不足时补充配方奶</li></ul><p><strong>睡眠管理</strong>：</p><ul><li>新生儿每日睡眠<code>16-20</code>小时</li><li>仰卧位睡眠最安全</li><li>保持室温适宜(<code>22-24℃</code>)</li></ul><p><strong>清洁护理</strong>：</p><ul><li><strong>洗澡</strong>：每日或隔日一次，水温<code>38-40℃</code></li><li><strong>脐带护理</strong>：<code>75%</code>酒精消毒，保持干燥</li><li><strong>臀部护理</strong>：及时更换尿布，预防红臀</li></ul><h4 id="新生儿常见问题处理" tabindex="-1">新生儿常见问题处理 <a class="header-anchor" href="#新生儿常见问题处理" aria-label="Permalink to “新生儿常见问题处理”">​</a></h4><p><strong>生理性黄疸</strong>：</p><ul><li>出现时间：出生后<code>2-3</code>天</li><li>高峰期：<code>4-5</code>天</li><li>消退时间：<code>7-10</code>天</li><li>处理：多晒太阳，增加喂养次数</li></ul><p><strong>肠绞痛</strong>：</p><ul><li>表现：每日固定时间哭闹，难以安抚</li><li>缓解方法：腹部按摩、飞机抱、白噪音</li></ul><p><strong>预防接种</strong>：</p><ul><li><strong>乙肝疫苗</strong>：出生后<code>24</code>小时内接种第一针</li><li><strong>卡介苗</strong>：出生后<code>24</code>小时内接种</li><li><strong>后续疫苗</strong>：按免疫规划程序进行</li></ul><p><strong>应急处理</strong>：</p><ul><li><strong>发热</strong>：体温&gt;<code>37.5℃</code>应就医</li><li><strong>呛奶</strong>：立即侧卧拍背，清理口鼻分泌物</li><li><strong>呼吸暂停</strong>：超过<code>20</code>秒应刺激复苏并就医</li></ul>',39)])])}const u=t(o,[["render",a]]);export{p as __pageData,u as default};
